import React, { useState } from "react";
import "../styles.css"; // Import the CSS file for styling
import F1_DATA from '..f1Data/data/f1Data';

const HamburgerMenu = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedTimeZone, setSelectedTimeZone] = useState("GMT+0"); // Default time zone

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  // Function to convert time based on the selected time zone
  const convertTime = (time, timeZoneOffset) => {
    const [hours, minutes] = time.split(":");
    const date = new Date();
    date.setHours(parseInt(hours, 10));
    date.setMinutes(parseInt(minutes, 10));
    date.setSeconds(0);

    // Adjust for the selected time zone
    const offsetHours = parseInt(timeZoneOffset.replace("GMT", ""), 10);
    date.setHours(date.getHours() + offsetHours);

    return date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
  };

  return (
    <div className="hamburger-menu">
      {/* Hamburger Icon */}
      <div
        className={`hamburger-icon ${isOpen ? "open" : ""}`}
        onClick={toggleMenu}
      >
        <span></span>
        <span></span>
        <span></span>
      </div>

      {/* Menu Overlay */}
      <div className={`menu-overlay ${isOpen ? "open" : ""}`}>
        <nav>
          <ul>
            <li>
              <a href="#home" onClick={toggleMenu}>
                Home
              </a>
            </li>
            <li>
              <a href="#about" onClick={toggleMenu}>
                About
              </a>
            </li>
            <li>
              <a href="#services" onClick={toggleMenu}>
                Services
              </a>
            </li>
            <li>
              <a href="#contact" onClick={toggleMenu}>
                Contact
              </a>
            </li>
            <li>
              {/* Time Zone Selector */}
              <label htmlFor="timeZone">Select Time Zone:</label>
              <select
                id="timeZone"
                value={selectedTimeZone}
                onChange={(e) => setSelectedTimeZone(e.target.value)}
              >
                {Array.from({ length: 26 }, (_, i) => i - 11).map((offset) => (
                  <option key={offset} value={`GMT${offset >= 0 ? "+" : ""}${offset}`}>
                    {`GMT${offset >= 0 ? "+" : ""}${offset}`}
                  </option>
                ))}
              </select>
            </li>
          </ul>
        </nav>

        {/* Display F1 Events with Adjusted Times */}
        <div className="f1-events">
          {F1_DATA.GrandsPrix.map((grandPrix) => (
            <div key={grandPrix.name} className="grand-prix">
              <h3>{grandPrix.name}</h3>
              <ul>
                {grandPrix.events.map((event, index) => (
                  <li key={index}>
                    <strong>{event.type}</strong>: {event.date} at{" "}
                    {convertTime(event.time, selectedTimeZone)}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HamburgerMenu;
