import React, { useState, useEffect } from 'react';
import '../styles.css';

const ScrollButtons = () => {
  const [showButtons, setShowButtons] = useState(false);

  // Function to scroll to the top of the page
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  // Function to scroll to the bottom of the page
  const scrollToBottom = () => {
    window.scrollTo({
      top: document.documentElement.scrollHeight,
      behavior: 'smooth',
    });
  };

  // Effect to show/hide buttons based on scroll position
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 100) {
        setShowButtons(true);
      } else {
        setShowButtons(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className={`scroll-buttons ${showButtons ? 'visible' : ''}`}>
      <button onClick={scrollToTop} className="scroll-button top-button">
        ↑
      </button>
      <button onClick={scrollToBottom} className="scroll-button bottom-button">
        ↓
      </button>
    </div>
  );
};

export default ScrollButtons;
