import React, { useState, useEffect } from 'react';
import GrandPrixDetails from './GrandPrixDetails';
import { fetchWithTimeout, API_BASE_URL } from '../api';

const GrandPrixButton = ({ isActive, setActive }) => {
  const [selectedGrandPrix, setSelectedGrandPrix] = useState(null);
  const [grandsPrix, setGrandsPrix] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (isActive && !selectedGrandPrix && grandsPrix.length === 0) {
      fetchGrandsPrix();
    }
  }, [isActive, selectedGrandPrix, grandsPrix]);

  const fetchGrandsPrix = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetchWithTimeout(`${API_BASE_URL}/api/sessions`);
      // Process the data to match the expected format
      const uniqueGrandsPrix = [...new Set(response.map(session => session[1]))];
      const formattedData = uniqueGrandsPrix.map(gpName => ({
        name: gpName,
        events: response
          .filter(session => session[1] === gpName)
          .map(session => ({
            type: session[2],  // session_type
            date: session[3],  // date
            time: session[4]   // time
          }))
      }));
      setGrandsPrix(formattedData);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleMoreDetailsClick = (grandPrix) => {
    setSelectedGrandPrix(grandPrix);
  };

  const handleGoBack = () => {
    setSelectedGrandPrix(null);
  };

  const handleHideAllDetails = () => {
    setActive();
    setSelectedGrandPrix(null);
  };

  return (
    <div className="grand-prix-button">
      <button onClick={handleHideAllDetails}>
        {isActive ? 'Hide Grand Prix Details' : 'Show Grand Prix Details'}
      </button>

      {isActive && (
        <div className="details-container">
          {loading ? (
            <p>Loading...</p>
          ) : error ? (
            <p>Error: {error}</p>
          ) : !selectedGrandPrix ? (
            grandsPrix.length > 0 ? (
              grandsPrix.map((grandPrix, index) => (
                <div key={index} className="grand-prix-container">
                  <h3>{grandPrix.name}</h3>
                  {grandPrix.events.map((event, eventIndex) => (
                    <div key={eventIndex} className="event-container">
                      <p>
                        {event.type}: {event.date} at {event.time}
                      </p>
                    </div>
                  ))}
                  <button
                    onClick={() => handleMoreDetailsClick(grandPrix)}
                    className="more-details-button"
                  >
                    MORE Details
                  </button>
                </div>
              ))
            ) : (
              <p>No Grand Prix events found.</p>
            )
          ) : (
            <GrandPrixDetails grandPrix={selectedGrandPrix} onGoBack={handleGoBack} />
          )}
        </div>
      )}
    </div>
  );
};

export default GrandPrixButton;
