// src/api.js
const API_BASE_URL = "http://localhost:5000"; // Replace with your Flask backend URL

export const fetchSessions = async () => {
  const response = await fetch(`${API_BASE_URL}/api/sessions`);
  if (!response.ok) {
    throw new Error("Failed to fetch sessions");
  }
  return response.json();
};

export const fetchSessionsByGrandPrix = async (grandPrixName) => {
  const response = await fetch(`${API_BASE_URL}/api/sessions/${grandPrixName}`);
  if (!response.ok) {
    throw new Error("Failed to fetch sessions by Grand Prix");
  }
  return response.json();
};
