import React, { useState } from 'react';
import DRIVER_SESSION_DATA from '../data/driverSessionData';

const GrandPrixButton = ({ data }) => {
  const [showDetails, setShowDetails] = useState(false);
  const [expandedSession, setExpandedSession] = useState(null);

  // Filter out Grand Prix with no future events
  const futureGrandPrix = data.GrandsPrix.filter((grandPrix) =>
    grandPrix.events.some((event) => {
      const eventDate = new Date(`${event.date} ${new Date().getFullYear()} ${event.time}`);
      return eventDate > new Date();
    })
  );

  const handleSessionClick = (grandPrixName, sessionType) => {
    if (expandedSession && expandedSession.grandPrixName === grandPrixName && expandedSession.sessionType === sessionType) {
      // If the same session is clicked again, collapse it
      setExpandedSession(null);
    } else {
      // Expand the clicked session
      setExpandedSession({ grandPrixName, sessionType });
    }
  };

  return (
    <div className="grand-prix-button">
      <button onClick={() => setShowDetails(!showDetails)}>
        {showDetails ? 'Hide All Grand Prix Details' : 'Show All Grand Prix Details'}
      </button>

      {showDetails && (
        <div className="details-container">
          {futureGrandPrix.length > 0 ? (
            futureGrandPrix.map((grandPrix, index) => (
              <div key={index} className="grand-prix-container">
                <h3>{grandPrix.name}</h3>
                {grandPrix.events.map((event, eventIndex) => {
                  const eventDate = new Date(`${event.date} ${new Date().getFullYear()} ${event.time}`);
                  if (eventDate > new Date()) {
                    return (
                      <div key={eventIndex} className="event-container">
                        <p>
                          {event.type}: {event.date} at {event.time}
                        </p>
                        <button
                          onClick={() => handleSessionClick(grandPrix.name, event.type)}
                          className="session-button"
                        >
                          {expandedSession &&
                          expandedSession.grandPrixName === grandPrix.name &&
                          expandedSession.sessionType === event.type
                            ? 'Hide Driver Data'
                            : 'Show Driver Data'}
                        </button>
                        {expandedSession &&
                          expandedSession.grandPrixName === grandPrix.name &&
                          expandedSession.sessionType === event.type && (
                            <div className="driver-data-container">
                              <table>
                                <thead>
                                  <tr>
                                    <th>Position</th>
                                    <th>Driver</th>
                                    <th>Time</th>
                                  </tr>
                                </thead>
                                <tbody>
                                  {DRIVER_SESSION_DATA[grandPrix.name][event.type].map(
                                    (data, index) => (
                                      <tr key={index}>
                                        <td>{data.position}</td>
                                        <td>{data.driver}</td>
                                        <td>{data.time}</td>
                                      </tr>
                                    )
                                  )}
                                </tbody>
                              </table>
                            </div>
                          )}
                      </div>
                    );
                  }
                  return null;
                })}
              </div>
            ))
          ) : (
            <p>No upcoming Grand Prix events found.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default GrandPrixButton;
