import React, { useState } from 'react';

const GrandPrixButton = ({ data }) => {
  const [showDetails, setShowDetails] = useState(false);

  // Filter out Grand Prix with no future events
  const futureGrandPrix = data.GrandsPrix.filter((grandPrix) =>
    grandPrix.events.some((event) => {
      const eventDate = new Date(`${event.date} ${new Date().getFullYear()} ${event.time}`);
      return eventDate > new Date();
    })
  );

  return (
    <div className="grand-prix-button">
      <button onClick={() => setShowDetails(!showDetails)}>
        {showDetails ? 'Hide All Grand Prix Details' : 'Show All Grand Prix Details'}
      </button>

      {showDetails && (
        <div className="details-container">
          {futureGrandPrix.length > 0 ? (
            futureGrandPrix.map((grandPrix, index) => (
              <div key={index} className="grand-prix-container">
                <h3>{grandPrix.name}</h3>
                {grandPrix.events.map((event, eventIndex) => {
                  const eventDate = new Date(`${event.date} ${new Date().getFullYear()} ${event.time}`);
                  if (eventDate > new Date()) {
                    return (
                      <div key={eventIndex} className="event-container">
                        <p>
                          {event.type}: {event.date} at {event.time}
                        </p>
                      </div>
                    );
                  }
                  return null;
                })}
              </div>
            ))
          ) : (
            <p>No upcoming Grand Prix events found.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default GrandPrixButton;
