import React, { useState } from 'react';
import { fetchWithTimeout, API_BASE_URL } from '../api';

const DriverStandings = ({ isActive, setActive }) => {
  const [standings, setStandings] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const toggleStandings = async () => {
    if (isActive) {
      setActive(); // Close if already active
    } else {
      setLoading(true);
      setError(null);
      try {
        const data = await fetchWithTimeout(`${API_BASE_URL}/api/driver_standings`);
        setStandings(data);
        setActive(); // Set as active
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="standings-container">
      <div className='grand-prix-button'>
      <button onClick={toggleStandings} disabled={loading}>
        {loading ? 'Loading...' : isActive ? 'Hide Driver Standings' : 'Show Driver Standings'}
      </button>
      </div>

      {error && <p className="error">{error}</p>}

      {isActive && standings.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Position</th>
              <th>Driver</th>
              <th>Team</th>
              <th>Points</th>
            </tr>
          </thead>
          <tbody>
            {standings.map((standing, index) => (
              <tr key={index}>
                <td>{standing.position}</td>
                <td>{standing.driver}</td>
                <td>{standing.team}</td>
                <td>{standing.points}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default DriverStandings;
