import React, { useState, useEffect } from 'react';
import { fetchSessionsByGrandPrix } from '../api'; // Correct import path

const GrandPrixDetails = ({ grandPrixName, onGoBack }) => {
  const [expandedSession, setExpandedSession] = useState(null);
  const [sessions, setSessions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch session data for the selected Grand Prix
  useEffect(() => {
    const loadData = async () => {
      try {
        const data = await fetchSessionsByGrandPrix(grandPrixName);
        setSessions(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    loadData();
  }, [grandPrixName]);

  const handleSessionClick = (sessionType) => {
    if (expandedSession === sessionType) {
      setExpandedSession(null);
    } else {
      setExpandedSession(sessionType);
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div className="grand-prix-details">
      <h2>{grandPrixName}</h2>
      {sessions.length > 0 ? (
        sessions.map((session, index) => (
          <div key={index} className="session-container">
            <button
              onClick={() => handleSessionClick(session.session_type)}
              className="session-button"
            >
              {session.session_type} {expandedSession === session.session_type ? '▲' : '▼'}
            </button>
            {expandedSession === session.session_type && (
              <table>
                <thead>
                  <tr>
                    <th>Position</th>
                    <th>Driver</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{session.pos}</td>
                    <td>{session.driver}</td>
                    <td>{session.time}</td>
                  </tr>
                </tbody>
              </table>
            )}
          </div>
        ))
      ) : (
        <p className="no-data-message">No session data available for this Grand Prix.</p>
      )}
      <button onClick={onGoBack} className="go-back-button">
        Go back
      </button>
    </div>
  );
};

export default GrandPrixDetails;
