// src/components/DriverStandings.js
import React, { useState } from 'react';
import { fetchWithTimeout, API_BASE_URL } from '../api'; // Correct import

const DriverStandings = () => {
  const [standings, setStandings] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchData = async () => {
    setLoading(true);
    try {
      const data = await fetchWithTimeout(`${API_BASE_URL}/api/driver_standings`);
      setStandings(data);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ margin: '20px', padding: '20px' }}>
      <h2>Driver Standings</h2>
      <button onClick={fetchData} disabled={loading}>
        {loading ? 'Loading...' : 'Load Standings'}
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {standings.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Position</th>
              <th>Driver</th>
              <th>Team</th>
              <th>Points</th>
            </tr>
          </thead>
          <tbody>
            {standings.map((standing, index) => (
              <tr key={index}>
                <td>{standing.position}</td>
                <td>{standing.driver}</td>
                <td>{standing.team}</td>
                <td>{standing.points}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default DriverStandings;
