import React, { useState, useEffect } from "react";
import Header from "./components/Header";
import NextRace from "./components/NextRace";
import NextEvent from "./components/NextEvent";
import GrandPrixButton from "./components/GrandPrixButton";
import F1_DATA from "./data/f1Data";
import ScrollButtons from "./components/ScrollButtons";
import HamburgerMenu from "./components/HamburgerMenu";
import "./styles.css";

const App = () => {
  const [nextRace, setNextRace] = useState(null);
  const [nextEvent, setNextEvent] = useState(null);
  const [raceTimeLeft, setRaceTimeLeft] = useState({ days: 0, hours: 0, minutes: 0, seconds: 0 });
  const [eventTimeLeft, setEventTimeLeft] = useState({ days: 0, hours: 0, minutes: 0, seconds: 0 });
  const [selectedTimeZone, setSelectedTimeZone] = useState("GMT+0"); // Default time zone

  // Function to adjust time based on the selected time zone
  const adjustTimeForTimeZone = (time, timeZoneOffset) => {
    const [hours, minutes] = time.split(":");
    const date = new Date();
    date.setHours(parseInt(hours, 10));
    date.setMinutes(parseInt(minutes, 10));
    date.setSeconds(0);

    // Adjust for the selected time zone
    const offsetHours = parseInt(timeZoneOffset.replace("GMT", ""), 10);
    date.setHours(date.getHours() + offsetHours);


    const convertTime = (time, timeZoneOffset) => {
      const [hours, minutes] = time.split(":");
      const date = new Date();
      date.setHours(parseInt(hours, 10));
      date.setMinutes(parseInt(minutes, 10));
      date.setSeconds(0);

      // Adjust for the selected time zone
      const offsetHours = parseInt(timeZoneOffset.replace("GMT", ""), 10);
      date.setHours(date.getHours() + offsetHours);

      return date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
    };

    return date;
  };

  // Function to find the next race and next event
  const findNextRaceAndEvent = () => {
    const today = new Date();
    let closestRace = null;
    let closestEvent = null;
    let closestRaceDiff = Infinity;
    let closestEventDiff = Infinity;

    F1_DATA.GrandsPrix.forEach((grandPrix) => {
      grandPrix.events.forEach((event) => {
        // Adjust the event time for the selected time zone
        const adjustedEventTime = adjustTimeForTimeZone(event.time, selectedTimeZone);
        const eventDate = new Date(`${event.date} ${today.getFullYear()} ${adjustedEventTime.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })}`);
        const diff = eventDate - today;

        if (diff > 0) {
          // Find the next race
          if (event.type === "Grand Prix" && diff < closestRaceDiff) {
            closestRaceDiff = diff;
            closestRace = { ...event, grandPrix: grandPrix.name };
          }

          // Find the next event (any type)
          if (diff < closestEventDiff) {
            closestEventDiff = diff;
            closestEvent = { ...event, grandPrix: grandPrix.name };
          }
        }
      });
    });

    setNextRace(closestRace);
    setNextEvent(closestEvent);
  };

  // Re-calculate next race and event when time zone changes
  useEffect(() => {
    findNextRaceAndEvent();
  }, [selectedTimeZone]);

  // Update the countdown timer for the next race
  useEffect(() => {
    if (nextRace) {
      const timer = setInterval(() => {
        const now = new Date();
        const adjustedRaceTime = adjustTimeForTimeZone(nextRace.time, selectedTimeZone);
        const raceDate = new Date(`${nextRace.date} ${now.getFullYear()} ${adjustedRaceTime.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })}`);
        const diff = raceDate - now;

        setRaceTimeLeft({
          days: Math.floor(diff / (1000 * 60 * 60 * 24)),
          hours: Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
          minutes: Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((diff % (1000 * 60)) / 1000),
        });
      }, 1000);

      return () => clearInterval(timer); // Cleanup timer on unmount
    }
  }, [nextRace, selectedTimeZone]);

  // Update the countdown timer for the next event
  useEffect(() => {
    if (nextEvent) {
      const timer = setInterval(() => {
        const now = new Date();
        const adjustedEventTime = adjustTimeForTimeZone(nextEvent.time, selectedTimeZone);
        const eventDate = new Date(`${nextEvent.date} ${now.getFullYear()} ${adjustedEventTime.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })}`);
        const diff = eventDate - now;

        setEventTimeLeft({
          days: Math.floor(diff / (1000 * 60 * 60 * 24)),
          hours: Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
          minutes: Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((diff % (1000 * 60)) / 1000),
        });
      }, 1000);

      return () => clearInterval(timer); // Cleanup timer on unmount
    }
  }, [nextEvent, selectedTimeZone]);

  return (
    <div className="app">
      <Header />
      <HamburgerMenu
        selectedTimeZone={selectedTimeZone}
        setSelectedTimeZone={setSelectedTimeZone}
      />

      {nextRace && (
        <NextRace
          nextRace={nextRace}
          timeLeft={raceTimeLeft}
          selectedTimeZone={selectedTimeZone}
        />
      )}

      {nextEvent && nextEvent.type !== "Grand Prix" && (
        <NextEvent
          nextEvent={nextEvent}
          timeLeft={eventTimeLeft}
          selectedTimeZone={selectedTimeZone}

        />
      )}

      <GrandPrixButton
        selectedTimeZone={selectedTimeZone}
        convertTime={convertTime} 
      />

      <ScrollButtons />
    </div>
  );
};

export default App;
