import React from 'react';
import CountdownTimer from './CountdownTimer';

const GrandPrixItem = ({ grandPrix }) => {
  const nextEvent = grandPrix.events[0]; // Assuming the first event is the next one

  return (
    <div className="grand-prix-item">
      <h2>{grandPrix.name}</h2>
      <p>Next Event: {nextEvent.type} on {nextEvent.date} at {nextEvent.time}</p>
      <CountdownTimer targetDate={`${nextEvent.date} ${nextEvent.time}`} />
    </div>
  );
};

export default GrandPrixItem;
