import React, { useState } from 'react';
import { fetchWithTimeout, API_BASE_URL } from '../api';

const DriverStandings = () => {
  const [standings, setStandings] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showStandings, setShowStandings] = useState(false);

  const toggleStandings = async () => {
    if (showStandings) {
      // Hide immediately if already shown
      setShowStandings(false);
    } else {
      // Only fetch when showing
      setLoading(true);
      setError(null);
      try {
        const data = await fetchWithTimeout(`${API_BASE_URL}/api/driver_standings`);
        setStandings(data);
        setShowStandings(true);
      } catch (err) {
        setError(err.message);
        setShowStandings(false);
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div>
      <button onClick={toggleStandings} disabled={loading}>
        {loading ? 'Loading...' : showStandings ? 'Hide Standings' : 'Show Standings'}
      </button>

      {error && <p>{error}</p>}

      {showStandings && standings.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Position</th>
              <th>Driver</th>
              <th>Team</th>
              <th>Points</th>
            </tr>
          </thead>
          <tbody>
            {standings.map((standing, index) => (
              <tr key={index}>
                <td>{standing.position}</td>
                <td>{standing.driver}</td>
                <td>{standing.team}</td>
                <td>{standing.points}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default DriverStandings;
